.contact-form__text-field:hover + .contact-form__label, .contact-form__text-field:focus + .contact-form__label, .contact-form__text-field:not(:placeholder-shown) + .contact-form__label, .contact-form__comment:focus + .contact-form__label, .contact-form__comment:hover + .contact-form__label, .contact-form__comment:not(:placeholder-shown) + .contact-form__label {
  color: #d41443;
}
.contact-form__text-field:hover + .contact-form__label, .contact-form__text-field:focus + .contact-form__label, .contact-form__text-field:not(:placeholder-shown) + .contact-form__label, .contact-form__comment:focus + .contact-form__label, .contact-form__comment:hover + .contact-form__label, .contact-form__comment:not(:placeholder-shown) + .contact-form__label {
  transform: translateY(-30px);
}
.contact-form__text-field:hover, .contact-form__text-field:focus, .contact-form__text-field:not(:placeholder-shown), .contact-form__comment:focus, .contact-form__comment:hover, .contact-form__comment:not(:placeholder-shown) {
  border-color: #d41443;
  outline: none;
}

.contact-form__icon-checkbox {
  position: absolute;
  left: 0;
  top: calc(18.228px / 2);
  transform: translateY(-50%);
  opacity: 1;
}
.contact-form__checkbox:focus ~ .contact-form__icon-checkbox {
  fill: #d31342;
}
.contact-form__chekcbox:checked + .contact-form__icon-checkbox {
  opacity: 0;
}

.contact-form__icon-checked {
  position: absolute;
  left: 0;
  opacity: 0;
  fill: #d31342;
}
.contact-form__checkbox:checked ~ .contact-form__icon-checked {
  opacity: 1;
}

.contact-form__label-agreement {
  padding-left: 20px;
  font-style: inherit;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.302;
}

.agreement-link {
  color: #d41443;
}

.contact-form__btn-submit {
  display: block;
  margin-left: auto;
  margin-right: auto;
  font-style: inherit;
  font-weight: 700;
  font-size: 22px;
  line-height: 1.302;
  padding: 10px 60px;
}

.order-modal {
  transform: translate(-50%, -50%);
  opacity: 1;
  transition: opacity 500ms cubic-bezier(0.4, 0, 0.2, 1), transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
}
.backdrop.is-hidden .order-modal {
  transform: translate(250%, 250%);
  opacity: 0;
  transition: opacity 500ms cubic-bezier(0.4, 0, 0.2, 1), transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
}
.order-modal.not-visible {
  pointer-events: none;
  opacity: 0;
  transform: translate(-300%, -300%);
  transition: opacity 500ms cubic-bezier(0.4, 0, 0.2, 1), transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
}

.order__heading {
  margin-bottom: 20px;
}

.order-list__item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}
.order-list__item:not(:last-child) {
  padding-bottom: 20px;
  border-bottom: 1px solid #e18298;
}

.order__item-description {
  flex-basis: 50%;
  font-style: inherit;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.302;
  color: #303030;
  text-transform: capitalize;
}
.order__item-price {
  margin-top: 10px;
  margin-bottom: 10px;
  text-align: right;
}
.order__item-input {
  width: 70%;
  padding: 9px 20px 9px 10px;
  font-style: inherit;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.302;
  border: 1px solid #303030;
  border-radius: 5px;
  transition: border-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  margin-left: 10px;
}
.order__item-input:focus, .order__item-input:hover {
  border-color: #d41443;
  outline: none;
}

.order-image {
  width: 140px;
}
@media screen and (min-width: 768px) {
  .order-image {
    width: 210px;
  }
}

.quantity-wrapper {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.order-sub-btn,
.order-calc-btn {
  margin-left: auto;
  margin-right: auto;
  font-style: inherit;
  font-weight: 700;
  font-size: 22px;
  line-height: 1.302;
  padding: 10px 60px;
}
@media screen and (max-width: 479.98px) {
  .order-sub-btn,
.order-calc-btn {
    padding: 10px 6vw;
  }
}

.order-calc-btn {
  margin-right: 10px;
}

.order__cost {
  align-items: center;
  margin-top: 50px;
  display: flex;
  justify-content: center;
  font-style: inherit;
  font-weight: 700;
  font-size: 22px;
  line-height: 1.302;
  color: #303030;
}

.total-value {
  display: block;
  margin-left: 30px;
}

.order__btn-wrapper {
  margin-top: 50px;
  display: flex;
  justify-content: center;
}

.map-modal {
  transform: translate(-50%, -50%);
  opacity: 1;
  background-color: #fff5f6;
  width: 95vw;
  padding: 60px 20px 20px 20px;
}
@media screen and (min-width: 480px) {
  .map-modal {
    width: 456px;
  }
}
@media screen and (min-width: 768px) {
  .map-modal {
    width: 729.6px;
  }
}
@media screen and (min-width: 1280px) {
  .map-modal {
    width: 1216px;
  }
}
.backdrop.is-hidden .map-modal {
  opacity: 0;
  transform: translate(250%, 250%);
}

.modal-location-list__item {
  position: absolute;
  top: 0;
  opacity: 0;
  pointer-events: none;
  transform: scale(0.1);
  transition: transform 1s linear;
}
.modal-location-list__item--active {
  position: static;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;
  align-items: center;
  opacity: 1;
  transform: scale(1);
  pointer-events: all;
}

.modal-map__iframe {
  width: 100%;
  border-radius: 20px;
  margin-bottom: 20px;
}
@media screen and (min-width: 480px) {
  .modal-map__iframe {
    width: 406px;
    height: 406px;
  }
}
@media screen and (min-width: 768px) {
  .modal-map__iframe {
    width: 679.6px;
    height: 406px;
  }
}
@media screen and (min-width: 1280px) {
  .modal-map__iframe {
    width: 1166px;
    height: 406px;
  }
}

.modal-map__contacts-place,
.modal-map__contacts-caption {
  text-align: center;
  margin-bottom: 20px;
}

.modal-map__buttons-wrapper {
  display: flex;
  justify-content: space-evenly;
}

