// Я поставив DM Sans, як домінуючий шрифт перебивати потрібно тільки де потрібно.
// В інших випадках не ставьте тільки задавайте weight
body {
  font-family: $primary-font-family, sans-serif;
  color: $text-color-primary;
}

// body.modal-open {
//   overflow: hidden;
// }

p,
h1,
h2,
h3,
h4,
h5,
h6 {
  margin: 0;
}

ul,
ol {
  list-style: none;
  margin: 0;
  padding: 0;
}

button {
  cursor: pointer;
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}

a {
  text-decoration: none;
}

//   ====reset для UL========

.visually-hidden {
  position: absolute;
  white-space: nowrap;
  width: 1px;
  height: 1px;
  overflow: hidden;
  border: 0;
  padding: 0;
  clip: rect(0 0 0 0);
  clip-path: inset(50%);
  margin: -1px;
}

