// ==============BACKGROUND-COLORS=============
$bg-primary: #ffffff;
$bg-dark: #e1e1e1;
$bg-dark-mobile: #fafafa;
$bg-accent-primary: #ffb8ca;
$bg-accent-primary-light: #fff5f6;
$bg-accent-second: #f0d1a5;
$bg-accent-third: #c2e297;
$bg-menu: #e17992;
$bg-hero-ellipse: #ffa5ba;
$bg-customer-icon: #d9d9d9;

// =============Border-COLORS======================
$border-color-light: #e1e1e1;
