%button {
    display: block;
    font-family: inherit;
    text-align: center;
    border: 0;

    transition: background-color $animation;
}

%button__primary {
    padding: 11px 40px;
    border-radius: 22px;

    font-weight: 700;
    font-size: 16px;
    line-height: 1.31;

    &::after {
        display: inline-block;

        content: '';
        height: 10px;
