.about {
  background-color: $bg-accent-primary-light;


  // ______________+++++
  @media screen and (max-width: 767px) {
    background-image: url(../images/about/about-left-450x1.png),
      url(../images/about/about-right-450x1.png);
    background-repeat: no-repeat, no-repeat;
    background-position: 0% 67%, 100% 73%;
  }


  @media screen and (min-device-pixel-ratio: 2),
  (min-resolution: 192dpi),
  (min-resolution: 2dppx) {

    background-image: url(../images/about/about-left-450x2.png),
      url(../images/about/about-right-450x2.png);
  }


  // __________++++++
  @media screen and (min-width: 1280px) {
    background-image: url(../images/about/about-left-1280x1.png),
      url(../images/about/about-right-1280x1.png);
    background-repeat: no-repeat, no-repeat;
    background-position: 0% 40%, 100% 95%;

    @media (min-device-pixel-ratio: 2),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {

      background-image: url(../images/about/about-left-1280x2.png), url(../images/about/about-right-1280x2.png);
    }
  }
