}

sources.sass\\components\\_advantages.scss:
.advantages {
    background-color: $bg-accent-primary-light;
}

.advantages__list {
    @media screen and (min-width: 768px) {
        display: flex;
    }

    @media screen and (min-width: 768px) and (max-width: 1279.98px) {
        width: 527px;
    }

    @media screen and (min-width: 1280px) {
        padding-right: 107px;
    }
}

.advantages__item {
    width: 230px;

    @media screen and (max-width: 767.98px) {
        &:not(:last-child) {
            margin-bottom: 32px;
        }
    }

    @media screen and (min-width: 768px) and (max-width: 1279.98px) {
        &:not(:last-child) {
            margin-right: 4px;
        }
    }

    @media screen and (min-width: 1280px) {
        width: 100%;

        &:not(:last-child) {
            margin-right: 124px;
        }
    }

    &:first-child::before {
        margin-bottom: 16px;
        display: inline-block;
        content: '';
        width: 33px;
        height: 50px;
        background-image: url(../images/advantages/png/icon-liter-1x.png);

        @media (min-device-pixel-ratio: 2),
        (-webkit-min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/advantages/png/icon-liter-2x.png);
        }

        background-size: cover;
        background-repeat: no-repeat;

        @media screen and (min-width: 768px) {
            margin-bottom: 36px;
        }

        @media screen and (min-width: 1280px) {
            margin-bottom: 40px;
        }
    }

    &:nth-child(2n):before {
        margin-bottom: 16px;
        display: inline-block;
        content: '';
        width: 50px;
        height: 50px;
        background-image: url(../images/advantages/png/icon-kg-1x.png);

        @media (min-device-pixel-ratio: 2),
        (-webkit-min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/advantages/png/icon-kg-2x.png);
        }

        background-size: cover;
        background-repeat: no-repeat;

        @media screen and (min-width: 768px) {
            margin-bottom: 36px;
        }

        @media screen and (min-width: 1280px) {
            margin-bottom: 40px;
        }
    }

    &:last-child::before {
        margin-bottom: 16px;
        display: inline-block;
        content: '';
        width: 44px;
        height: 50px;
        background-image: url(../images/advantages/png/icon-hurt-1x.png);

        @media (min-device-pixel-ratio: 2),
        (-webkit-min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/advantages/png/icon-hurt-2x.png);
        }

        background-size: cover;
        background-repeat: no-repeat;

        @media screen and (min-width: 768px) {
            margin-bottom: 36px;
        }

        @media screen and (min-width: 1280px) {
            margin-bottom: 40px;
        }
    }
}

.advantages__caption {
    display: block;
    margin-bottom: 13px;
    color: $text-color-accent;
    font-family: $secondary-font-family;
    font-size: 36px;
    line-height: 1.14;
    letter-spacing: 0.04em;

    @media screen and (max-width: 767.98px) {
        text-transform: uppercase;
    }

    @media screen and (min-width: 768px) {
        font-size: 48px;
        line-height: 1.15;
    }

    @media screen and (min-width: 1280px) {
        margin-bottom: 16px;
        font-size: 58px;
        line-height: 1.14;
    }
}

.advantages__text {
    font-weight: 700;
    font-size: 12px;
    line-height: 1.67;

    @media screen and (min-width: 768px) {
        font-size: 14px;
        line-height: 1.71;
    }

    @media screen and (min-width: 1280px) {
        font-size: 16px;
        line-height: 1.75;
    }
}

sources.sass\\components\\_contacts.scss:
.contacts { 
   
    background-image: linear-gradient(
        to bottom,
        $bg-primary 25%,
        $bg-accent-primary-light 25%,
        $bg-accent-primary-light 100%,
      
      );
}
.contacts__container{
    @media screen and (min-width: 768px) and (max-width: 1279.99px) {
    padding-left: 18px;
    padding-right: 18px;}
}
.container__list{
    display: flex;
    flex-direction: column;
    margin-bottom: 32px;
    justify-content: center;
    align-items: center;
    @media screen and (min-width: 768px) {
        flex-direction: row;
        margin-bottom: 40px
    }
}
.contacts__items{
    padding: 40px 32px;
    
    border-radius: 24px;
    background: $bg-primary;
    box-shadow: $shadow-card-contacts;
    @media screen and (max-width: 767px) { 
        width: 280px;
    } 
    @media screen and (min-width: 768px) {
        width: 224px    
        }
        @media screen and (min-width: 1280px) {
            width: 395px;
            padding: 40px 40px; 
            
            }
      
    &:not(:last-child){
        @media screen and (max-width: 767px) {
        margin-bottom: 20px;
        }
        @media screen and (min-width: 768px) {
            margin-right: 32px;
               
            }
      }
}

.contacts__place {
    display: inline-block;
    margin-bottom: 20px;
    padding: 1px 23px;
    border-radius: 4px;
    text-align: center;
    letter-spacing: 0.04em;
    color: $text-color-secondary;
    background-color: $bg-accent-second;
    @include family-vr(null, 500, 12px, null);
    @media screen and (min-width: 768px) {
        margin-bottom: 32px;
        @include family-vr(null, 500, 16px, 1.86); 
        }
    &--foodtruck{ 
        padding: 1px 26px;
        background-color: $bg-accent-third;
       }
}

.contacts__caption {
    margin-bottom: 8px;
    letter-spacing: 0.04em;
    @include family-vr(normal, 500, 12px, 1.86);
    color: $text-color-primary;
    @media screen and (min-width: 768px) and (max-width: 1279.99px) {
        margin-bottom: 12px;
        @include family-vr(null, 500, 14px, 1.86); 
        }
    @media screen and (min-width: 1280px) {
    @include family-vr(null, 500, 16px, 1.86); 
    }    
}

.contacts__text{
    padding-bottom: 24px;
    border-bottom: 1px solid $border-color-light;
    @include family-vr(null, 500, 12px, 18px);
    color: $text-color-dark;
    @media screen and (min-width: 768px) and (max-width: 1279.99px){
        padding-bottom: 32px;
        @include family-vr(null, 500, 14px, 1.66); 
        }
    @media screen and (min-width: 1280px) {
        padding-bottom: 32px;
        max-width: 243px;
        @include family-vr(null, 500, 16px, 1.83); 
        }     
}
.schedule {
    padding-top: 24px;
    padding-bottom: 24px;
    border-bottom: 1px solid $border-color-light;
    @media screen and (min-width: 768px) {
        padding-top: 32px;
        padding-bottom: 32px;   
    }
}
.schedule__list--margin{
    margin-bottom: 8px;
    @media screen and (min-width: 768px) {
        margin-bottom: 12px; 
    }
}
.schedule__text{
    @include family-vr(null, 500, 12px, 1.86);
    color: $text-color-primary;
    @media screen and (min-width: 768px) {
        @include family-vr(null, 500, 14px, 1.86);
        }
        @media screen and (min-width: 1280px) {
            @include family-vr(null, 500, 16px, 1.86);
            }
    &--color{ 
        color:$text-color-accent-light;}

}
 
.callback{
    margin-top: 24px;
    @media screen and (min-width: 768px) {
        margin-top: 32px;
    }
}
.callback__link{
    @include family-vr(null, 500, 12px, 18px);
    color: $text-color-dark;
    @media screen and (min-width: 768px) {
        @include family-vr(null, 500, 14px, 1.83); 
        }
    @media screen and (min-width: 1280px) {
        @include family-vr(null, 500, 16px, 1.83); 
        }
}

.contacts__btn{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    @media screen and (min-width: 768px) {
        flex-direction: row;
        }
    &-item:not(:last-child){
        @media screen and (max-width: 767px) {
        margin-bottom: 10px;
    }
    @media screen and (min-width: 768px) {
        margin-right: 14.5px;
        }
    }
   
  
}
.button_contacts{
    @media screen and (max-width: 479.99px) {
    padding: 11px 0;
    min-width: 162px;}
   
}

sources.sass\\utils\\_mixins.scss:
//  Коли ви вносите дані в змінну ВАЖЛИВA! послідовність:
// 1.font-style, 2.font-weight, 3.font-size, 4.line-height
// Якщо виникла ситуація коли деякий параметр не потрібно ставити => (null, 400, 18px, 21px), потрібно поставити null.

@mixin family-vr($fst, $fw, $fsz, $lh) {
  font-style: $fst;
  font-weight: $fw;
  font-size: $fsz;
  line-height: $lh;
}

@mixin border($color) {
  border: 1px solad $color;
}

@mixin btn-color ($bg, $color) {
  background-color: $bg;
  color: $color;
}

sources.sass\\components\\_customer-reviews.scss:
.swiper-slide {
    display: flex;
    justify-content: center;
    align-items: center;
}

.customer__article {
    text-align: center;
    display: block;
    width: 100%;
}

.customer__img {
    margin-left: auto;
    margin-right: auto;
    border-radius: 50%;
}

.customer__content {
    padding-top: 32px;

    @media screen and (min-width: 768px) {
        padding-top: 20px;
    }

    @media screen and (min-width: 1280px) {
        padding-top: 16px;
        padding-left: 132px;
        padding-right: 132px;
    }
}

.customer__container {
    display: block;
    width: auto;
    height: auto;
    padding: 43px 50px 17px;
    margin-bottom: 33px;
    background-color: $bg-dark-mobile;

    @media screen and (min-width: 768px) {
        padding: 37px 6px 36px;
        background-color: transparent;
        margin-bottom: 0;
    }

    @media screen and (min-width: 1280px) {
        background-color: $bg-dark-mobile;
        padding: 32px 105px;
        margin-bottom: 16px;
    }

}

.customer__about {
    position: relative;
    font-weight: 500;
    height: 100%;
    width: 180px;
    margin-right: auto;
    margin-left: auto;
    background: repeating-linear-gradient($bg-dark-mobile, $bg-dark-mobile 30px, $bg-dark 30px, $bg-dark 31px);

    @media screen and (min-width: 768px) {
        width: 608px;
        background: repeating-linear-gradient($bg-primary, $bg-primary 29px, $bg-dark 29px, $bg-dark 30px);
    }

    @media screen and (min-width: 1280px) {
        width: 822px;
        background: repeating-linear-gradient($bg-dark-mobile, $bg-dark-mobile 29px, $bg-dark 29px, $bg-dark 30px);
    }

    &:before {
        position: absolute;
        display: inline;
        content: '';
        height: 40px;
        width: 54px;
        top: -20px;
        left: -35px;
        background-image: url('../images/customer-reviews/quotes.svg');
        background-repeat: no-repeat;
        background-size: contain;

        @media screen and (min-width: 768px) {
            left: 0px;
        }

        @media screen and (min-width: 1280px) {

            left: -10px;
        }
    }

}

.customer__about-text {
    display: block;
    width: 157px;
    margin: auto;
    font-size: 12px;
    line-height: 2.58;

    @media screen and (min-width: 768px) {
        width: 514px;
        font-size: 14px;
        line-height: 2.14;
    }

    @media screen and (min-width: 1280px) {
        width: 750px;
        font-size: 16px;
        line-height: 1.88;
    }

}

.customer__name {
    font-weight: 500;
    font-size: 14px;
    line-height: 1.86;

    @media screen and (min-width: 768px) {
        font-size: 16px;
        line-height: 1.86;
    }

    @media screen and (min-width: 1280px) {
        font-size: 20px;
        line-height: 1.85;
    }


    &::before {
        display: block;
        content: '';
        height: 4px;
        width: 32px;
        margin: auto;
        margin-bottom: 16px;

        background-image: url('../images/customer-reviews/dots.svg');
        background-repeat: no-repeat;
        background-size: contain;
    }
}

.swiper-pagination-bullet {
    width: 12px;
    height: 12px;
    line-height: 20px;
    background-color: $icon-color-accent-dark;
    margin-top: 50px;
}

.swiper-pagination-bullet:hover {
    width: 12.4px;
    height: 12px;
    border-radius: 0;
    background-color: transparent;
    background-image: url('../images/customer-reviews/home.svg');
}

.swiper-pagination-bullet-active {
    width: 12.4px;
    height: 12px;
    border-radius: 0;
    background-color: transparent;
    background-image: url('../images/customer-reviews/home.svg');
}

.swiper-pagination {
    position: relative;
    top: 16px;
    margin-bottom: 16px;

    @media screen and (min-width: 768px) {
        margin-bottom: 32px;
    }

    @media screen and (min-width: 1280px) {
        top: 32px;
    }
}

sources.sass\\components\\_footer.scss:
.footer {
    background-color: $bg-accent-primary-light;
}

.footer__container {
    padding-bottom: 40px;

    @media screen and (min-width: 768px) {
        display: flex;
        justify-content: space-between;

    }

    @media screen and (min-width: 1280px) {
        padding-bottom: 60px;
        align-items: flex-end;

    }
}

.footer__title {

    @media screen and (max-width: 767.98px) {
        margin-bottom: 40px;
        width: 234px;
    }

    @media screen and (min-width: 768px) and (max-width: 1279.98px) {
        width: 340px;

    }

    font-size: 18px;
    font-weight: 400;
    line-height: 1.17;
    letter-spacing: 0.04em;
    text-transform: uppercase;
    font-family: $secondary-font-family;
    color: $text-color-accent-light;

    @media screen and (min-width: 768px) {
        font-size: 26px;
        line-height: 1.16;

    }

    @media screen and (min-width: 1280px) {
        font-size: 30px;
        line-height: 1.14;
    }

}

.footer__text--color {
    display: block;
    width: 200px;
    font-size: 34px;
    line-height: 1.15;
    letter-spacing: 0.04em;
    text-transform: uppercase;
    margin-top: 16px;
    color: $text-color-accent;

    @media screen and (min-width: 768px) and (max-width: 1279px) {
        margin-top: 12px;

    }

    @media screen and (min-width: 768px) {
        font-size: 48px;
        width: 340px;

    }

    @media screen and (min-width: 1280px) {

        width: 474px;
        font-size: 58px;
        margin-top: 16px;
    }


}

.networks {
    display: flex;
    flex-direction: column;
    align-items: flex-end;

}

.networks__list {
    display: flex;
    margin-bottom: 40px;
}

.networks__iteam:not(:last-child) {
    margin-right: 28px;

    @media screen and (min-width: 768px) {
        margin-right: 30px;
    }

    @media screen and (min-width: 1280px) {
        margin-right: 25px;
    }
}

.networks__link {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 32px;
    height: 32px;
    fill: $bg-primary;
    background-color: $networks-color;
    border-radius: 50%;

    &:hover,
    &:focus {
        background-color: $networks-color-hover;
    }


    transition: background-color $animation;

    @media screen and (min-width: 768px) {
        width: 34px;
        height: 34px;
    }

}

.footer__contact--link {

    font-style: normal;
    font-weight: 700;
    font-size: 12px;
    line-height: 1.86;
    letter-spacing: 0.02em;

    color: $text-color-primary;

    @media screen and (min-width: 768px) {
        font-size: 14px;
    }

    @media screen and (min-width: 1280px) {
        font-size: 16px;
    }
}

.footer__contact--color {
    color: $text-color-accent;
}

.footer__wrap {
    padding: 16px 0 20px;
    border-top: 1px solid $border-color-light;

    @media screen and (min-width: 480px) {
        padding: 8px 0;
    }
}

.copyright {
    margin-left: auto;
    margin-right: auto;
    width: auto;
    font-style: normal;
    font-weight: 700;
    font-size: 14px;
    line-height: 1.86;
    text-align: center;

    color: $text-color-dark;

    @media screen and (max-width: 767px) {
        width: 145px;
        line-height: 1.52;
    }

    @media screen and (min-width: 767px) and (max-width: 1280px) {
        letter-spacing: 0.02em;
    }
}

sources.sass\\components\\_gallery.scss:
.gallery {
    background-image: linear-gradient(180deg, $bg-accent-primary-light, $bg-accent-primary-light 220px, $bg-primary 0);

    @media screen and (min-width: 480px) {
      background-image: linear-gradient(180deg, $bg-accent-primary-light, $bg-accent-primary-light 350px, $bg-primary 0)
    }
  }

.grid {
  margin: 0 auto;
}

.grid:after {
  content: '';
  display: block;
  clear: both;
}

.grid__wrap {
  float: left;
}

.grid__wrap,
.grid__img {
  width: 60px;
  height: 60px;

  border-radius: 16px;
  overflow: hidden;
  margin-bottom: 4px;


  @media screen and (min-width: 353px) {
    width: 80px;
    height: 80px;
  }

  @media screen and (min-width: 480px) {
    width: 120px;
    height: 120px;
    margin-bottom: 6px;
  }

  @media screen and (min-width: 768px) {
    width: 160px;
    height: 160px;
    margin-bottom: 2px;
  }

  @media screen and (min-width: 1280px) {
    width: 180px;
    height: 180px;
    margin-bottom: 2px;
  }
}

.grid__img {
  max-width: none;
  transition: transform 350ms cubic-bezier(0.4, 0, 0.2, 1), 
              width 350ms cubic-bezier(0.4, 0, 0.2, 1), 
              height 350ms cubic-bezier(0.4, 0, 0.2, 1);
}

.grid__img:hover {
  position: relative;
  transform: scale(1.05);
  cursor: pointer;
}

.is-expanded.grid__wrap,
.is-expanded .grid__img {
  height: 120px;
  width: 120px;

  @media screen and (min-width: 480px) {
    width: 200px;
    height: 200px;
  }

  @media screen and (min-width: 768px) {
    width: 280px;
    height: 280px;
  }

  @media screen and (min-width: 1280px) {
    width: 320px;
    height: 320px;
  }
}

sources.sass\\components\\_header.scss:
.page-header {
  position: relative;
  z-index: 900;
}
.page-header__container {
  background-color: transparent;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding-top: 8px;
  padding-bottom: 0;
  position: absolute;
  left: 50%;
  top: 0;
  transform: translateX(-50%);

  @media screen and (min-width: 768px) {
    padding-top: 32px;
  }
  @media screen and (min-width: 1280px) {
    padding-top: 37px;
  }
}
.header__buy-btn {
  &::after {
    margin-left: 0;
  }

  &:hover,
  &:focus {
    background-color: $button-color-hover;
  }
  @media screen and (min-width: 768px) {
    display: block;
  }
}
.menu-btn {
  border: none;
  background-color: transparent;
  padding: 0;
  display: flex;
  @media screen and (min-width: 768px) {
    margin-right: 32px;
  }
  @media screen and (min-width: 1280px) {
    display: none;
  }
}
.menu-btn__icon {
  fill: $button-color-accent;
  transition: fill $animation;
  .menu-btn:hover &,
  .menu-bnt:focus & {
    fill: $button-color;
  }
}

.header__logo {
  text-transform: uppercase;
  font-family: 'Titan One';
  font-style: normal;
  font-weight: 400;
  font-size: 26px;
  line-height: 30px;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  display: flex;
  align-items: center;

  color: $text-color-accent;
  @media screen and (min-width: 768px) and (max-width: 1279.98px) {
    flex-grow: 2;
  }
}
.logo__img {
  width: 50px;
  height: 50px;
  background-color: $bg-primary;
  border-radius: 50%;
  margin-right: 20px;
}
.site-nav {
  &__item {
    padding-left: 20px;
    border-top: 1px solid $border-color-accent;
    &:last-child {
      border-bottom: 1px solid $border-color-accent;
    }
  }
  &__link {
    padding-top: 11px;
    padding-bottom: 11px;
    @include family-vr(inherit, 700, 14px, 1.302);

    display: flex;
    align-items: center;

    color: $text-color-secondary;

    transition: color $animation;

    &:hover,
    &:focus,
    &.current {
      color: $text-color-accent;
    }
  }
}

.header__site-nav {
  @media screen and (max-width: 1279.98px) {
    display: none;
  }
}
.header-site-nav {
  &__list {
    display: flex;
  }
  &__item:not(:last-child) {
    margin-right: 35px;
  }
  &__link {
    @include family-vr(inherit, 700, 16px, 1.302);
  }
}

sources.sass\\components\\_hero.scss:
.hero {
  padding-top: 194px;
  padding-bottom: 158px;

  background-color: $bg-accent-primary;

  overflow: hidden;

  @media screen and (min-width: 768px) {
    padding-top: 102px;
    padding-bottom: 56px;
  }

  @media screen and (min-width: 1280px) {
    padding-top: 147px;
    padding-bottom: 84px;
  }
}

.hero__container {
  position: relative;
}

.hero__ellipse {
  position: absolute;
  bottom: -83px;
  right: -20%;

  width: 346px;
  height: 344px;
  border-radius: 50%;

  background: $bg-hero-ellipse;
  box-shadow: $shadow-ellipse-hero;

  animation: pulsing 10s infinite;

  @media screen and (min-width: 768px) {
    bottom: -16px;
    right: 24%;

    width: 335px;
    height: 333px;
  }

  @media screen and (min-width: 1280px) {
    bottom: -8px;
    right: 18%;

    width: 534px;
    height: 534px;
  }

  @keyframes pulsing {
    15% {
      transform: scale(1, 1);
    }

    17% {
      transform: scale(1.1, 1);
    }

    18% {
      transform: scale(1, 1);
    }

    22% {
      transform: scale(1, 1);
    }

    24% {
      transform: scale(1, 1.1);
    }

    25% {
      transform: scale(1, 1);
    }

    31% {
      transform: scale(1, 1);
    }

    33% {
      transform: scale(1.2, 1.1);
    }

    34% {
      transform: scale(1, 1);
    }

    100% {
      transform: scale(1, 1);
    }
  }
}

.hero__ice-cream-img {
  position: absolute;
  bottom: -158px;
  right: 10%;

  @media screen and (min-width: 768px) {
    bottom: -56px;
    right: 39%;
  }

  @media screen and (min-width: 1280px) {
    bottom: -84px;
    right: 32%;
  }
}

.hero__so-tasty-img {
