.popup-menu {
  width: 250px;
  height: 100vh;
  background-color: $bg-menu;
  position: fixed;
  right: 0;
  padding-top: 60px;
  z-index: 999;

  transition: opacity $animation, transform $animation;

  @media screen and (min-width: 768px) {
    width: 40vw;
    padding-top: 58px;
  }

  &__close-btn {
    display: flex;
    border: none;
    background-color: transparent;
    position: absolute;
    top: 20px;
    right: 20px;
  }
}
.close-btn__icon {
  fill: $button-color;
}
.is-hidden {
  opacity: 0;
  pointer-events: none;
  transform: translateX(255px);
}

sources.sass\\components\\_back-to-top.scss:
.back-to-top {
  position: fixed;
  bottom: 20px;
  right: 20px;

  background-color: transparent;
  border-radius: 50%;
  z-index: 900;
  border: none;
  opacity: 1;
  transform: translate(0) scale(1) rotate(0);

  transition: opacity $animation-modal, transform $animation-modal;
  &:hover,
  &:focus {
    outline: none;
  }

  &.hidden {
    opacity: 0;
    transform: translate(200px, 200px) scale(5) rotate(720deg);
    transition: opacity $animation-modal, transform $animation-modal;
    pointer-events: none;
  }
  &__icon {
    fill: $networks-color-hover;

    .back-to-top:hover &,
    .back-to-top:focus & {
      fill: $networks-color;
    }
  }
}

sources.sass\\components\\_modal.scss:
.backdrop {
  z-index: 10000;
  position: fixed;
  top: 0;
  width: 100vw;
  height: 100vh;
  background: rgba(25, 28, 38, 0.2);
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.04), 0px 2px 4px rgba(0, 0, 0, 0.08),
    0px 1px 3px rgba(0, 0, 0, 0.16);
  opacity: 1;
  transition: opacity $animation-modal;
}
.is-hidden {
  opacity: 0;
  pointer-events: none;
  transition: opacity $animation-modal;
}
.no-scroll {
  overflow: hidden;
}

.modal {
  max-width: 93.75vw;
  max-height: 95vh;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(250%, 250%);
  background-color: $bg-primary;
  border-radius: 20px;
  border: 5px solid $networks-color-hover;
  padding: 60px 30px 20px 20px;
  opacity: 0;
  overflow-y: auto;
  transition: opacity $animation-modal, transform $animation-modal;

  &.visible {
    opacity: 1;
    transform: translate(-50%, -50%);
    transition: opacity $animation-modal, transform $animation-modal;
  }
  .backdrop.is-hidden & {
    transform: translate(250%, 250%);
    opacity: 0;
    transition: opacity $animation-modal, transform $animation-modal;
  }

  @media screen and (min-width: 768px) {
    max-width: 80vw;
  }

  &__close-btn {
    position: absolute;
    top: 20px;
    right: 20px;
    padding: 0;
    display: flex;

    border: none;
    background-color: transparent;

    transition: fill $animation;

    &-icon {
      fill: #303030;
      .modal__close-btn:hover &,
      .modal__close-btn:focus & {
        fill: $icon-color-accent-light;
      }
    }
  }

  &__heading {
    color: $text-color-accent;
    text-align: center;
    margin-bottom: 50px;
  }
}

sources.sass\\components\\_buy-modal.scss:
.contact-form {
  @media screen and (min-width: 1280px) {
    display: flex;
    flex-wrap: wrap;
  }
  &__form-field {
    position: relative;
    margin-bottom: 30px;

    color: #303030;

    @media screen and (min-width: 1280px) {
      flex-basis: calc((100% - 60px) / 3);
      &:nth-child(-n + 2) {
        margin-right: 30px;
      }
      &:nth-child(-n + 3) {
        margin-bottom: 30px;
      }
      &:last-of-type {
        flex-basis: 100%;
      }
    }
  }
  &__form-area {
    position: relative;
    @media screen and (min-width: 1280px) {
      flex-basis: 100%;
    }
  }
  &__text-field {
    width: 100%;
    height: 36px;
    padding: 9px 10px;
    @include family-vr(inherit, 700, 14px, 1.302);
    border: 1px solid #303030;
    border-radius: 5px;
  }
  &__label {
    position: absolute;
    top: 9px;
    left: 10px;
    @include family-vr(inherit, 700, 14px, 1.302);
    transition: color $animation, transform $animation;
  }
  &__comment {
    width: 100%;
    height: 100px;
    padding: 9px 10px;
    margin-bottom: 30px;

    border: 1px solid #303030;
    border-radius: 5px;
    resize: none;
  }

  &__text-field:hover + &__label,
  &__text-field:focus + &__label,
  &__text-field:not(:placeholder-shown) + &__label,
  &__comment:focus + &__label,
  &__comment:hover + &__label,
  &__comment:not(:placeholder-shown) + &__label {
    color: $text-color-accent;
  }

  &__text-field:hover + &__label,
  &__text-field:focus + &__label,
  &__text-field:not(:placeholder-shown) + &__label,
  &__comment:focus + &__label,
  &__comment:hover + &__label,
  &__comment:not(:placeholder-shown) + &__label {
    transform: translateY(-30px);
  }

  &__text-field:hover,
  &__text-field:focus,
  &__text-field:not(:placeholder-shown),
  &__comment:focus,
  &__comment:hover,
  &__comment:not(:placeholder-shown) {
    border-color: $text-color-accent;
    outline: none;
  }
}

.contact-form__icon-checkbox {
  position: absolute;
  left: 0;
  top: calc(18.228px / 2);
  transform: translateY(-50%);

  opacity: 1;

  .contact-form__checkbox:focus ~ & {
    fill: $icon-color-accent;
  }
  .contact-form__chekcbox:checked + & {
    opacity: 0;
  }
}
.contact-form__icon-checked {
  position: absolute;
  left: 0;

  opacity: 0;
  fill: $icon-color-accent;

  .contact-form__checkbox:checked ~ & {
    opacity: 1;
  }
}
.contact-form__label-agreement {
  padding-left: 20px;
  @include family-vr(inherit, 700, 14px, 1.302);
}
.agreement-link {
  color: $text-color-accent;
}

.contact-form__btn-submit {
  @extend %button;
  display: block;
  margin-left: auto;
  margin-right: auto;
  @include family-vr(inherit, 700, 22px, 1.302);
  padding: 10px 60px;
}
///////////////////////////////////
.order-modal {
  transform: translate(-50%, -50%);
  opacity: 1;
  transition: opacity $animation-modal, transform $animation-modal;

  .backdrop.is-hidden & {
    transform: translate(250%, 250%);
    opacity: 0;
    transition: opacity $animation-modal, transform $animation-modal;
  }

  &.not-visible {
    pointer-events: none;
    opacity: 0;
    transform: translate(-300%, -300%);
    transition: opacity $animation-modal, transform $animation-modal;
  }
}
.order__heading {
  margin-bottom: 20px;
}

.order-list__item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;

  &:not(:last-child) {
    padding-bottom: 20px;
    border-bottom: 1px solid $border-color-accent;
  }
}
.order__item {
  &-description {
    flex-basis: 50%;

    @include family-vr(inherit, 700, 14px, 1.302);
    color: #303030;
    text-transform: capitalize;
  }
  &-price {
    margin-top: 10px;
    margin-bottom: 10px;
    text-align: right;
  }
  &-input {
    width: 70%;
    padding: 9px 20px 9px 10px;
    @include family-vr(inherit, 700, 14px, 1.302);
    border: 1px solid #303030;
    border-radius: 5px;
    transition: border-color $animation;
    margin-left: 10px;

    &:focus,
    &:hover {
      border-color: $networks-color-hover;
      outline: none;
    }
  }
}
.order-image {
  width: 140px;
  @media screen and (min-width: 768px) {
    width: 210px;
  }
}
.quantity-wrapper {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.order-sub-btn,
.order-calc-btn {
  margin-left: auto;
  margin-right: auto;
  @include family-vr(inherit, 700, 22px, 1.302);
  padding: 10px 60px;

  @media screen and (max-width: 479.98px) {
    padding: 10px 6vw;
  }
}
.order-calc-btn {
  margin-right: 10px;
}
.order__cost {
  align-items: center;
  margin-top: 50px;
  display: flex;
  justify-content: center;
  @include family-vr(inherit, 700, 22px, 1.302);
  color: #303030;
}

.total-value {
  display: block;

  margin-left: 30px;
}
.order__btn-wrapper {
  margin-top: 50px;
  display: flex;
  justify-content: center;
}

sources.sass\\components\\_map-modal.scss:
////////////////////////////////////////////////////
.map-modal {
  transform: translate(-50%, -50%);
  opacity: 1;
  background-color: #fff5f6;
  width: 95vw;
  padding: 60px 20px 20px 20px;
  @media screen and (min-width: 480px) {
    width: 456px;
  }
  @media screen and (min-width: 768px) {
    width: 729.6px;
  }
  @media screen and (min-width: 1280px) {
    width: 1216px;
  }

  .backdrop.is-hidden & {
    opacity: 0;
    transform: translate(250%, 250%);
  }
}

.modal-location-list {
  &__item {
    position: absolute;
    top: 0;
    opacity: 0;
    pointer-events: none;
    transform: scale(0.1);
    transition: transform 1s linear;

    &--active {
      position: static;
      display: flex;
      flex-wrap: wrap;
      justify-content: space-evenly;
      align-items: center;
      opacity: 1;
      transform: scale(1);
      pointer-events: all;
    }
  }
}
.modal-map__iframe {
  width: 100%;
  border-radius: 20px;
  margin-bottom: 20px;

  @media screen and (min-width: 480px) {
    width: 406px;
    height: 406px;
  }
  @media screen and (min-width: 768px) {
    width: 679.6px;
    height: 406px;
  }
  @media screen and (min-width: 1280px) {
    width: 1166px;
    height: 406px;
  }
}

.modal-map__contacts-place,
.modal-map__contacts-caption {
  text-align: center;
  margin-bottom: 20px;
}
.modal-map__buttons-wrapper {
  display: flex;
  justify-content: space-evenly;
}
